SELECT الدعاوى.matterAR, الدعاوى.[client&Cap], الدعاوى.[opponent&Cap], [admin work table].المحكمة, [admin work table].الدائرة, [admin work table].الجهة, الجلسات.التاريخ, الجلسات.القرار, [admin work table].[العمل المطلوب], [admin work table].الحالة, [admin work table].[تاريخ الإنشاء], qryMaxAdminFollowUp2.[تاريخ الإجراء], qryMaxAdminFollowUp2.[القائم بالعمل], qryMaxAdminFollowUp2.النتيجة
FROM (الدعاوى INNER JOIN ([admin work table] LEFT JOIN qryMaxAdminFollowUp2 ON [admin work table].ID_Task=qryMaxAdminFollowUp2.ID_Task) ON الدعاوى.matterID=[admin work table].matterID) LEFT JOIN الجلسات ON الدعاوى.matterID=الجلسات.matterID
WHERE ((([admin work table].الجهة)=Forms!Dashboard!التقارير.Form!txtDestination) And (([admin work table].الحالة)<>"منجزة") And ((الجلسات.تقرير)=Yes));
